      pet: { name: 'Animaux', icon: 'üêæ', cards: [] },
      housing: { name: 'Logement', icon: 'üè†', cards: [] },
      study: { name: '√âtudes', icon: 'üìö', cards: [] },
      travel: { name: 'Voyages', icon: '‚úàÔ∏è', cards: [] },
      other: { name: 'Autre', icon: 'üé¥', cards: [] }
