                )}
                {playerData?.married && <div className="stat">üíí Mari√©(e)</div>}
                <div className="stat">‚ù§Ô∏è Flirts: {playerData?.flirts.length}/5</div>
                <div className="stat">üë∂ Enfants: {playerData?.children.length}</div>
                <div className="stat">üêæ Animaux: {playerData?.pets.length}</div>
                <div className="stat">üí∞ Salaires: {playerData?.salaryCount}</div>
              </div>

              {/* Cartes pos√©es */}
              <div className="played-cards">
                <h4>Cartes jou√©es</h4>
                <div className="cards-grid">
                  {playerData?.playedCards.map((card, index) => (
                    <div 
                      key={index} 
                      className={`mini-card ${card.isMalus ? 'malus' : ''} ${card.type === 'job' ? 'job-card' : ''}`} 
                      title={card.description}
                    >
                      <div className="card-emoji">{getCardEmoji(card)}</div>
                      <div className="card-name">{card.name}</div>
                      {card.smiles > 0 && <div className="card-mini-smiles">üòä {card.smiles}</div>}
                    </div>
                  ))}
                </div>
              </div>

              {/* Main du joueur */}
              <div className="hand">
                <h4>Votre main</h4>
                <div className="cards-row">
                  {playerData?.hand.map((card, index) => (
                    <div
                      key={index}
                      className={`card-hand ${selectedCardIndex === index ? 'selected' : ''}`}
                      onClick={() => setSelectedCardIndex(index)}
                    >
                      <div className="card-emoji-large">{getCardEmoji(card)}</div>
                      <div className="card-info">
                        <div className="card-name">{card.name}</div>
                        <div className="card-smiles">üòä {card.smiles || 0}</div>
                        {card.type === 'travel' && card.cost && (
                          <div className="card-cost">üí∞ Co√ªt: {card.cost}</div>
                        )}
                        <div className="card-desc">{card.description}</div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Chat */}
          <div className="chat-area">
            <h3>üí¨ Chat</h3>
            <div className="messages">
              {messages.map((msg, index) => (
                <div key={index} className={`message ${msg.type}`}>
                  {msg.type === 'chat' ? (
                    <><strong>{msg.playerName}:</strong> {msg.message}</>
                  ) : (
                    <em>{msg.message}</em>
                  )}
                </div>
              ))}
              <div ref={chatEndRef} />
            </div>
            <form onSubmit={sendMessage} className="chat-input-form">
              <input
                type="text"
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                placeholder="Tapez votre message..."
                className="chat-input"
              />
              <button type="submit" className="btn-send">Envoyer</button>
            </form>
          </div>
        </div>

        {error && <div className="error-toast">{error}</div>}

        {/* Modal pour parier au casino */}
        {showCasinoBet && playerData?.salary && playerData.salary.length > 0 && (
          <div className="modal-overlay" onClick={() => setShowCasinoBet(false)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <h3>üé∞ Choisissez un salaire √† parier</h3>
              <div className="casino-bet-grid">
                {playerData.salary.map((salaryCard, index) => (
                  <div 
                    key={index} 
                    className="casino-bet-card"
                    onClick={() => placeCasinoBet(index)}
                  >
                    <div className="card-emoji-large">{getCardEmoji(salaryCard)}</div>
                    <div className="card-name">{salaryCard.name}</div>
                    <div className="card-value">Valeur: {salaryCard.salaryValue || 1}</div>
                  </div>
                ))}
              </div>
              <button className="btn btn-secondary" onClick={() => setShowCasinoBet(false)}>
                Annuler
              </button>
            </div>
          </div>
        )}

        {showCasinoBet && (!playerData?.salary || playerData.salary.length === 0) && (
          <div className="modal-overlay" onClick={() => setShowCasinoBet(false)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <h3>‚ùå Aucun salaire √† parier</h3>
              <p>Vous n'avez pas de salaire √† parier au casino !</p>
              <button className="btn btn-secondary" onClick={() => setShowCasinoBet(false)}>
                Fermer
              </button>
            </div>
          </div>
        )}
        {showDiscardPicker && (
          <div className="modal-overlay" onClick={() => setShowDiscardPicker(false)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <h3>üçÄ Chance ! Choisissez une carte de la d√©fausse</h3>
              <div className="discard-picker-grid">
                {gameData?.discardPile.slice().reverse().map((card, index) => {
                  const actualIndex = gameData.discardPile.length - 1 - index;
                  return (
                    <div 
                      key={actualIndex} 
                      className="discard-picker-card"
                      onClick={() => takeDiscardCard(actualIndex)}
                    >
                      <div className="card-emoji-large">{getCardEmoji(card)}</div>
                      <div className="card-name">{card.name}</div>
                      <div className="card-smiles">üòä {card.smiles || 0}</div>
                    </div>
                  );
                })}
              </div>
              <button className="btn btn-secondary" onClick={() => setShowDiscardPicker(false)}>
                Annuler
              </button>
            </div>
          </div>
        )}
      </div>
    );
  }

  // Rendu de fin de partie
  if (gameState === 'gameover') {
    const renderStat = (statData, emoji, label) => {
      if (!statData || statData.length === 0) return null;
      return (
        <div className="stat-item">
          <span className="stat-emoji">{emoji}</span>
          <span className="stat-label">{label}:</span>
          <span className="stat-values">
            {statData.map((p, i) => (
              <span key={i}>{p.name} ({p.value}){i < statData.length - 1 && ', '}</span>
            ))}
          </span>
        </div>
      );
    };

    return (
      <div className="App">
        {/* Confettis */}
        <Confetti active={showConfetti} />

        <div className="container">
          <h1 className="title">üèÜ Partie termin√©e üèÜ</h1>
          <div className="gameover-card">
            <h2>ü•á Classement final</h2>
            <div className="scores-list">
              {gameData?.finalScores?.map((score, index) => (
                <div key={index} className={`score-item rank-${index + 1}`}>
                  <span className="rank">
                    {index === 0 && 'ü•á'}
                    {index === 1 && 'ü•à'}
                    {index === 2 && 'ü•â'}
                    {index > 2 && `${index + 1}.`}
                  </span>
                  <span className="player-name">{score.name}</span>
                  <span className="score">üòä {score.smiles}</span>
                </div>
              ))}
            </div>

            {gameData?.stats && (
              <div className="stats-section">
                <h3>üìä Statistiques de la partie</h3>
                <div className="stats-grid">
                  {renderStat(gameData.stats.mostMalus, 'üíî', 'Plus de malus subis')}
                  {renderStat(gameData.stats.mostStudies, 'üìö', 'Plus haut niveau d\'√©tudes')}
                  {renderStat(gameData.stats.mostSalaryEnd, 'üí∞', 'Plus de salaires √† la fin')}
                  {renderStat(gameData.stats.mostSalaryTotal, 'üíµ', 'Plus de salaires durant la partie')}
                  {renderStat(gameData.stats.mostTravels, '‚úàÔ∏è', 'Plus de voyages')}
                  {renderStat(gameData.stats.mostFlirts, '‚ù§Ô∏è', 'Plus de flirts')}
                  {renderStat(gameData.stats.mostChildren, 'üë∂', 'Plus d\'enfants')}
                  {renderStat(gameData.stats.mostPets, 'üêæ', 'Plus d\'animaux')}
                  {renderStat(gameData.stats.mostJobs, 'üíº', 'Plus de m√©tiers durant la partie')}
                  {renderStat(gameData.stats.mostMarriages, 'üíí', 'Plus de mariages durant la partie')}
                </div>

                <h3 style={{marginTop: '30px'}}>üìà Stats d√©taill√©es de tous les joueurs</h3>
                <div className="all-stats-table">
                  {gameData.stats.allPlayers?.map((player, idx) => (
                    <div key={idx} className="player-stats-row">
                      <div className="player-stats-name">{player.name}</div>
                      <div className="player-stats-details">
                        <span>üòä {player.smiles}</span>
                        <span>üìö {player.studies}</span>
                        <span>üíî {player.malus}</span>
                        <span>üí∞ {player.salaryEnd}</span>
                        <span>üíµ {player.salaryTotal}</span>
                        <span>‚úàÔ∏è {player.travels}</span>
                        <span>‚ù§Ô∏è {player.flirts}</span>
                        <span>üë∂ {player.children}</span>
                        <span>üêæ {player.pets}</span>
                        <span>üíº {player.jobs}</span>
                        <span>üíí {player.marriages}</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <button 
              onClick={() => window.location.reload()} 
              className="btn btn-primary"
            >
              Retour au menu
            </button>
          </div>
        </div>
      </div>
    );
  }

  return null;
}

export default App;
